import React from "react";
import { useState, useEffect } from "react";
import SortSelection from "./SortSelection";
import HeroesList from "./HeroesList";
import withAPIRequest from "../WithAPIRequest/WithAPIRequest";

const HeroesSelect = ({
  data,
  updateCurrentHero
}) => {
  const [sortByName, setSortByName] = useState(true);
  useEffect(() => {
    sortHeroesList();
  }, [sortByName]);

  const sortHeroesList = () => {
    data.sort((first, second) => {
      return sortByName ? first.localized_name.toLowerCase() > second.localized_name.toLowerCase() ? 1 : -1 : first.roles[0].toLowerCase() > second.roles[0].toLowerCase() ? 1 : -1;
    });
  };

  const toggleSortType = () => setSortByName(!sortByName);

  return React.createElement("div", null, React.createElement(SortSelection, {
    sortByName: sortByName,
    onChange: toggleSortType
  }), React.createElement(HeroesList, {
    updateCurrentHero: updateCurrentHero,
    heroes: data
  }));
};

export default withAPIRequest(HeroesSelect, () => ({
  url: "heroStats"
}));
